package com.whilerain.tinychart.utilsimport android.content.res.Resourcesclass UiUtil {    companion object {        fun dpToPx(dp: Int): Int {            return (dp * Resources.getSystem().displayMetrics.density).toInt()        }        fun getRuntimeDescription(timeSecondFromStart: Float): String{            return run {                val sec = (timeSecondFromStart % 60).toInt()                val min = ((timeSecondFromStart % 3600) / 60).toInt()                val hour = (timeSecondFromStart / 3600).toInt()                "${String.format("%02d", hour)}:${String.format(                    "%02d",                    min                )}:${String.format("%02d", sec)}"            }        }    }}